import { useEffect, useState } from "react";
import axios from "axios";
import './App.scss';
import Tabs from '../Tabs/Tabs'
import Tab from '../Tabs/Tab'

const leagues: string[] = [
  "English Premier League",
  "English League Championship",
  "Scottish Premier League",
  "German Bundesliga",
  "Italian Serie A",
];

interface team {
  logo: string;
  name: string;
}

const App = () => {
  const [data, setData] = useState<any>(null);

  useEffect(() => {
    const league_teams: any[] = [] 
    const temp = leagues.map(async(league, index) => {
      return (await axios("https://www.thesportsdb.com/api/v1/json/1/search_all_teams.php?l="+league)).data.teams;
      });
      setData(temp)
  }, []);

  console.log(data)
  console.log(data[0])

  return (
    <Tabs>
      <Tab title="English Premier League" teams={data}/>
      <Tab title= "English League Championship" teams={data}/>
      <Tab title="Scottish Premier League" teams={data}/>
      <Tab title="German Bundesliga" teams={data}/>
      <Tab title="Italian Serie A" teams={data}/>
    </Tabs>
  )
}

export default App;
