import { useEffect, useState } from "react";
import axios from "axios";
import './App.scss';
import Tabs from '../Tabs/Tabs'
import Tab from '../Tabs/Tab'

const leagues: string[] = [
  "English Premier League",
  "English League Championship",
  "Scottish Premier League",
  "German Bundesliga",
  "Italian Serie A",
];

interface team {
  logo: string;
  name: string;
}

const App = () => {
  const [data, setData] = useState<any>(null);

  useEffect(() => {
    const league_teams: any[] = [] 
    leagues.map(async(league, index) => {
     league_teams[index] = (await axios.get("https://www.thesportsdb.com/api/v1/json/1/search_all_teams.php?l="+league)).data.teams
      });
    setData(league_teams)
  }, []);

  console.log(data)

  return (
      <Tabs>
        <Tab teams={data} index={0}/>
        <Tab teams={data} index={1}/>
        <Tab teams={data} index={2}/>
        <Tab teams={data} index={3}/>
        <Tab teams={data} index={4}/>
      </Tabs>
  )
}

export default App;
