import { useEffect } from "react";
import axios from "axios";
import './App.scss';
import Tabs from '../Tabs/Tabs'
import Tab from '../Tabs/Tab'

const leagues: string[] = [
  "English Premier League",
  "English League Championship",
  "Scottish Premier League",
  "German Bundesliga",
  "Italian Serie A",
];

interface team {
  logo: string;
  name: string;
}

const App = () => {
  const league_teams: any = [] 
  useEffect(() => {
      leagues.map(async(league, index) => {
        league_teams[index] = (await axios("https://www.thesportsdb.com/api/v1/json/1/search_all_teams.php?l="+league)).data.teams;
        console.log(league_teams[index])
      });
      console.log(league_teams)
  }, []);

  return (
    <Tabs>
      <Tab title="English Premier League" teams={league_teams[0]}/>
      <Tab title= "English League Championship" teams={league_teams[1]}/>
      <Tab title="Scottish Premier League" teams={league_teams[2]}/>
      <Tab title="German Bundesliga" teams={league_teams[3]}/>
      <Tab title="Italian Serie A" teams={league_teams[4]}/>
    </Tabs>
  )
}

export default App;
